// @format

'use strict';

const debug = require('debug')('cogs:areFiguresAbleToReachEachOther');
const PF = require('pathfinding');
const _ = require('lodash');

module.exports = () => {
  return (req, res, next) => {
    (function init() {
      debug(
        '// Checks if parcel generated by landGenerateMapPost fullfills condition so figures are able to reach each other'
      );

      if (!res.locals.conditions) {
        res.locals.conditions = [];
      }

      res.locals.conditions.push((parcel) => {
        return validateCondition(parcel);
      });

      next();
    })();

    function validateCondition(parcel) {
      const figureCoordsArray = calculateFigureCoordsArray(parcel);
      const height = parcel.length;
      const width = parcel[0].length;
      const firstCoord = figureCoordsArray.shift(); // mutating

      let isPossible = true;
      figureCoordsArray.forEach((coord) => {
        const grid = new PF.Grid(width, height);

        // define collidables
        parcel.forEach((row, y) => {
          row.forEach((figure, x) => {
            if (figure === 'tree') {
              grid.setWalkableAt(x, y, false);
            }
          });
        });

        const finder = new PF.AStarFinder({ allowDiagonal: false });
        const path = finder.findPath(
          firstCoord.x,
          firstCoord.y,
          coord.x,
          coord.y,
          grid
        );

        if (_.isEmpty(path)) {
          isPossible = false;
        }
      });

      if (isPossible) {
        debug('validateCondition: Correct! All figures on parcel can reach each other');
      } else {
        debug('validateCondition: Wrong! Some figures on parcel cannot reach each other');
      }

      return isPossible;
    }

    function calculateFigureCoordsArray(parcel) {
      const figureCoordsArray = [];
      parcel.forEach((row, y) => {
        row.forEach((figure, x) => {
          if (figure !== 'empty' && figure !== 'tree') {
            figureCoordsArray.push({ x: x, y: y });
          }
        });
      });
      return figureCoordsArray;
    }
  };
};
